
shinyUI(fluidPage(
    tags$style(type='text/css',
               ".selectize-input { font-size: 12px; line-height: 12px;}
                .selectize-dropdown { font-size: 10px; line-height: 10px; }"),
    fluidRow(
        column(width = 5,
               fluidRow(sidebarPanel("Choose Settings to Plot:", width = 12)),
               fluidRow(column(4, selectInput("plot_type", "Value",
                                              choices = c("Matric Potential",
                                                  "Soil Moisture",
                                                  "Nitrate Concentration")
                                              )),
                        column(4,
                               selectInput("irr_sys", "Irrigation System",
                                           choices = c("Drip",
                                               "Fanjet"))),
                        column(4,
                               selectInput("time",
                                           "Irrigation Duration",
                                           choices = 0:24))), 
               fluidRow(column(4,
                               selectInput("n_fert", "N Fertilizer Type",
                                           choices = c("NO3"))),
                        column(4,
                               selectInput("flow_rate", "Flow Rate",
                                           choices = c("1 in/day",
                                               "2 in/day"))),
                        column(4, selectInput("fert_start",
                                              "Fert. Start Time",
                                              choices = c("none", "2", "11", "21")))),
               fluidRow(column(4, selectInput("fert_dur",
                                              "Fertigation Duration",
                                              choices = c("none", "3"))),
                        column(4, selectInput("applied_n",
                                              "Applied N (lb/ac)",
                                              choices = c("none", "50"))),
                        column(4, selectInput("irr_h2o_ec",
                                              "Irrigation Water EC",
                                              choices = c("none")))),
               fluidRow(column(4, selectInput("soil_type", "Soil Type",
                                              choices = c("Loam",
                                                  "Sand"))),
                        column(4, selectInput("unit", "Unit",
                                              choices = c("centimeters",
                                                  "inches")))),
               fluidRow(column(4),
                        column(4, submitButton("Submit")),
                        column(4))),
        column(width = 4, plotOutput("FlowTable",
                   height = 600, width = 700))
        )
    ))
